INSERT INTO `wp_postmeta` VALUES ('879', '122', '_shopkit_short_description', 'Jeans represent democracy in fashion. I wish I had invented blue jeans. They have expression, modesty, sex appeal, simplicity - all I hope for in my clothes. Clothes mean nothing until someone lives in them. Fashion is made to become unfashionable. Men don\'t want another man to look at their woman because they don\'t know how to handle it.');
INSERT INTO `wp_postmeta` VALUES ('880', '122', '_shopkit_post_quote', 'Jeans represent democracy in fashion. I wish I had invented blue jeans. They have expression, modesty, sex appeal, simplicity - all I hope for in my clothes. Clothes mean nothing until someone lives in them. Fashion is made to become unfashionable. Men don\'t want another man to look at their woman because they don\'t know how to handle it.');
